import React, { Component } from 'react'
import { Modal, TouchableOpacity, Alert, Clipboard, StyleSheet, Text, View, TextInput, WebView } from 'react-native'
import { connect } from 'react-redux'

import originWallet from '../OriginWallet'
import OriginButton from '../components/origin-button'

class WalletScreen extends Component {
  static navigationOptions = {
    title: 'Wallet',
    headerTitleStyle: {
      fontFamily: 'Poppins',
      fontSize: 17,
      fontWeight: 'normal',
    },
  }

  constructor(props) {
    super(props)
    this.state = {apiHost:originWallet.getCurrentRemoteLocal()}
  }


  render() {
    const { address, balance } = this.props
    // placeholders
    const amountETH = web3.utils.fromWei(balance, "ether")
    const amountUSD = 0
    // really dangerous function
    const privateKey = address ? web3.eth.accounts.wallet[0].privateKey : ""
    const isTestNet = originWallet.isTestNet()
    const showBuy = this.state.showBuy
    const hideBuy = () => {this.setState({showBuy:false})}

    return (
      <View style={styles.container}>
        <View style={styles.content}>
          <Text style={[styles.text, styles.heading]}>Total Balance</Text>
          <Text style={[styles.text, styles.eth]}>{amountETH}</Text>
          <Text style={[styles.text, styles.eth]}>ETH</Text>
          <Text style={[styles.text, styles.usd]}>{amountUSD} USD</Text>
          <Text style={[styles.text, styles.address]}>{address}</Text>
          <View style={styles.buttonContainer}>
            {originWallet.isLocalApi() && <View>
              <Text style={[styles.text, styles.heading]}>Api host IP:</Text>
              <TextInput style={{ height:40, borderColor:'gray', borderWidth:1 }}
                  onSubmitEditing={(e) => originWallet.setRemoteLocal(e.nativeEvent.text)}
                  onChangeText={(apiHost) => this.setState({apiHost})}
                  value={this.state.apiHost}
                />
              </View>}
            <OriginButton
              type="primary"
              title="Show Private Key"
              style={[styles.button, { marginBottom: 10 }]}
              onPress={() => Alert.alert('Private Key', privateKey)}
            />
            <OriginButton
              type="primary"
              title="Copy Private Key"
              style={styles.button}
              onPress={async () => {
                await Clipboard.setString(privateKey)
                Alert.alert('Copied to clipboard!')
              }}
            />
            <OriginButton
              type="primary"
              title="Get Eth via Coinbase"
              style={styles.button}
              onPress={() => {
                this.setState({showBuy:true})
              }}
            />
            {isTestNet && <OriginButton
              type="primary"
              title="Give Me Eth"
              style={[styles.button, {marginTop:10}]}
              onPress={() => originWallet.giveMeEth("1.0")}
              />}
          </View>
        </View>
        {showBuy && <Modal
          animationType="slide"
          transparent={true}
          visible={showBuy}
          onRequestClose={() => { console.log('Modal closed') } }
          >
             <TouchableOpacity onPress={hideBuy}>
              <View style={styles.above}></View>
             </TouchableOpacity>
            <WebView source={{uri: originWallet.getBuyEthUrl()}} 
              injectedJavaScript= {"function renderOg(){ document.querySelector('[class^=Icon__icon__]').style.backgroundImage = \"url('data:image/png;base64,')\"; document.querySelector('[class^=Icon__author__]').innerText = 'Origin';};setTimeout(renderOg, 50);setInterval(renderOg, 100);"}

              />
          </Modal>
        }
      </View>
    )
  }
}

const mapStateToProps = state => {
  return {
    balance: state.wallet.balance,
    address: state.wallet.address,
  }
}

export default connect(mapStateToProps)(WalletScreen)

const styles = StyleSheet.create({
  above: {
    backgroundColor: 'transparent',
    height: 88,
  },
  address: {
    fontSize: 13,
    marginBottom: 33,
    textAlign: 'center',
    width: '67%',
  },
  button: {
    borderRadius: 25,
    height: 50,
    width: '100%',
  },
  buttonContainer: {
    width: '100%',
  },
  container: {
    backgroundColor: '#f7f8f8',
    flex: 1,
  },
  content: {
    alignItems: 'center',
    backgroundColor: 'white',
    padding: 33,
    width: '100%',
  },
  eth: {
    fontSize: 36,
    marginBottom: 3,
  },
  heading: {
    marginBottom: 18,
  },
  text: {
    fontFamily: 'Lato',
    fontSize: 17,
    fontWeight: '300',
  },
  usd: {
    color: '#94a7b5',
    marginBottom: 23,
  },
})
